    /**
     * @param tempUnit the temperature unit to be used by the setpoint
     * temperature selector
     */
    public void setTemperatureUnit(Temperature.TempUnit tempUnit) {
        setpointTemperatureSelector.setTemperatureUnit(tempUnit);
    }

    /**
     * @param timeFormat the time format to be used by the time frame selector
     */
    public void setTimeFormat(Time.TimeFormat timeFormat) {
        timeFrameSelector.setTimeFormat(timeFormat);
    }

    /**
     * @return a program with the selected settings
     */
    public ACProgram getSelectedProgram() {
        ACProgram program = new ACProgram();

        program.setEnabled(toggleButton.isToggledOn());
        program.setDaysOfWeekSelection(daysOfWeekSelector.getSelection());
        program.setTimeFrame(timeFrameSelector.getSelectedTimeFrame());
        program.setSetpointTemp(setpointTemperatureSelector.getSelectedTemperature());
        program.setMode(modeSelector.getSelectedMode());
        program.setFanSpeed(fanSpeedSelector.getSelectedFanSpeed());

        return program;
    }

    /**
     * @param program the program whose settings will be preselected in the
     * selectors
     */
    public void setSelectedProgram(ACProgram program) {
        toggleButton.setToggledOn(program.isEnabled());
        daysOfWeekSelector.setSelection(program.getDaysOfWeekSelection());
        timeFrameSelector.setSelectedTimeFrame(program.getTimeFrame());
        setpointTemperatureSelector.setSelectedTemperature(program.getSetpointTemp());
        modeSelector.setSelectedMode(program.getMode());
        fanSpeedSelector.setSelectedFanSpeed(program.getFanSpeed());
    }
    
    public void setSelectedDeviceName(String name) {
        deviceLabel.setText(name);
    }

